import { onMount } from 'svelte';

  // State variables
  let apiKey = '';
  let searchQuery = '';
  let searchResults = [];
  let playlist = [];
  let currentTrack = null;
  let currentTrackIndex = -1;
  let isPlaying = false;
  let currentTime = 0;
  let duration = 0;
  let volume = 1;
  let isShuffled = false;
  let isRepeating = false;
  let quotaUsed = 0;
  let quotaLimit = 10000;
  let showSearchArea = true;

  // YouTube player variables
  let youtubePlayer = null;
  let playerReady = false;

  // Load data from localStorage on mount
  onMount(() => {
    const savedApiKey = localStorage.getItem('youtube-api-key');
    const savedPlaylist = localStorage.getItem('youtube-playlist');
    const savedQuota = localStorage.getItem('youtube-quota');
    const savedShowSearch = localStorage.getItem('show-search-area');

    if (savedApiKey) apiKey = savedApiKey;
    if (savedPlaylist) playlist = JSON.parse(savedPlaylist);
    if (savedQuota) quotaUsed = parseInt(savedQuota);
    if (savedShowSearch !== null) showSearchArea = JSON.parse(savedShowSearch);

    // Load YouTube iframe API
    if (!window.YT) {
      window.onYouTubeIframeAPIReady = initializePlayer;

      const tag = document.createElement('script');
      tag.src = 'https://www.youtube.com/iframe_api';
      document.head.appendChild(tag);
    } else if (window.YT && window.YT.Player) {
      initializePlayer();
    }
  });

  // Initialize YouTube player
  function initializePlayer() {
    if (document.getElementById('youtube-player')) {
      youtubePlayer = new YT.Player('youtube-player', {
        height: '1',
        width: '1',
        playerVars: {
          autoplay: 0,
          controls: 0,
          disablekb: 1,
          fs: 0,
          modestbranding: 1,
          rel: 0,
          iv_load_policy: 3
        },
        events: {
          onReady: () => { 
            playerReady = true;
          },
          onStateChange: onPlayerStateChange,
          onError: (event) => {
            console.error('YouTube player error:', event.data);
          }
        }
      });
    }
  }

  // Handle player state changes
  function onPlayerStateChange(event) {
    if (event.data === YT.PlayerState.PLAYING) {
      isPlaying = true;
      updateProgress();
    } else if (event.data === YT.PlayerState.PAUSED) {
      isPlaying = false;
    } else if (event.data === YT.PlayerState.ENDED) {
      nextTrack();
    }
  }

  // Update progress
  function updateProgress() {
    if (youtubePlayer && isPlaying && playerReady) {
      currentTime = youtubePlayer.getCurrentTime();
      duration = youtubePlayer.getDuration();

      if (isPlaying) {
        setTimeout(updateProgress, 1000);
      }
    }
  }

  // Toggle search area visibility
  function toggleSearchArea() {
    showSearchArea = !showSearchArea;
    localStorage.setItem('show-search-area', JSON.stringify(showSearchArea));
  }

  // Save API key
  function saveApiKey() {
    localStorage.setItem('youtube-api-key', apiKey);
  }

  // Search YouTube videos
  async function searchVideos() {
    if (!apiKey || !searchQuery) return;
    if (quotaUsed >= quotaLimit) return;

    try {
      const response = await fetch(
        `https://www.googleapis.com/youtube/v3/search?part=snippet&maxResults=10&q=${encodeURIComponent(searchQuery)}&type=video&key=${apiKey}`
      );

      if (!response.ok) throw new Error('Search failed');

      const data = await response.json();
      searchResults = data.items.map(item => ({
        id: item.id.videoId,
        title: item.snippet.title,
        channel: item.snippet.channelTitle,
        thumbnail: item.snippet.thumbnails.medium.url,
        url: `https://www.youtube.com/watch?v=${item.id.videoId}`
      }));

      quotaUsed += 100;
      localStorage.setItem('youtube-quota', quotaUsed.toString());
    } catch (error) {
      console.error('Search failed:', error);
    }
  }

  // Add to playlist
  function addToPlaylist(video) {
    if (!playlist.find(item => item.id === video.id)) {
      playlist = [...playlist, video];
      localStorage.setItem('youtube-playlist', JSON.stringify(playlist));
    }
  }

  // Remove from playlist
  function removeFromPlaylist(index) {
    playlist = playlist.filter((_, i) => i !== index);
    localStorage.setItem('youtube-playlist', JSON.stringify(playlist));

    if (currentTrackIndex === index) {
      currentTrack = null;
      currentTrackIndex = -1;
      isPlaying = false;
    } else if (currentTrackIndex > index) {
      currentTrackIndex--;
    }
  }

  // Play track
  function playTrack(track, index) {
    if (!window.YT || !window.YT.Player) return;
    if (!playerReady || !youtubePlayer) {
      setTimeout(() => playTrack(track, index), 1000);
      return;
    }

    currentTrack = track;
    currentTrackIndex = index;

    try {
      youtubePlayer.loadVideoById(track.id);
    } catch (error) {
      console.error('Error loading video:', error);
    }
  }

  // Toggle play/pause
  function togglePlayPause() {
    if (!currentTrack) {
      if (playlist.length > 0) {
        playTrack(playlist[0], 0);
      }
      return;
    }

    if (!playerReady || !youtubePlayer) return;

    if (isPlaying) {
      youtubePlayer.pauseVideo();
    } else {
      youtubePlayer.playVideo();
    }
  }

  // Previous track
  function previousTrack() {
    if (playlist.length === 0) return;

    let newIndex = currentTrackIndex - 1;
    if (newIndex < 0) newIndex = playlist.length - 1;

    playTrack(playlist[newIndex], newIndex);
  }

  // Next track
  function nextTrack() {
    if (playlist.length === 0) return;

    let newIndex;
    if (isShuffled) {
      newIndex = Math.floor(Math.random() * playlist.length);
    } else {
      newIndex = currentTrackIndex + 1;
      if (newIndex >= playlist.length) {
        newIndex = isRepeating ? 0 : playlist.length - 1;
      }
    }

    playTrack(playlist[newIndex], newIndex);
  }

  // Seek
  function seek(event) {
    if (!youtubePlayer || !playerReady || !duration) return;

    const rect = event.target.getBoundingClientRect();
    const percent = (event.clientX - rect.left) / rect.width;
    const seekTime = percent * duration;

    youtubePlayer.seekTo(seekTime, true);
  }

  // Handle keyboard events for seek
  function handleSeekKeydown(event) {
    if (event.key === 'Enter' || event.key === ' ') {
      event.preventDefault();
      seek(event);
    }
  }

  // Export playlist
  function exportPlaylist() {
    const urls = playlist.map(track => track.url).join('\n');
    const blob = new Blob([urls], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'youtube-playlist.txt';
    a.click();
    URL.revokeObjectURL(url);
  }

  // Format time
  function formatTime(seconds) {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  }

