@import url('https://fonts.googleapis.com/css2?family=Montserrat:wght@400;600;700;800&display=swap');

  /* Global styles */
  :global(body) {
    margin: 0;
    padding: 0;
    font-family: 'Montserrat', sans-serif;
    background: #101114;
    color: #ffffff;
  }

  .app {
    min-height: 100vh;
    opacity: 0;
    transition: opacity 0.3s ease-in-out;
  }

  .fade-in {
    opacity: 1;
  }

  /* Home View Styles */
  .home-view {
    min-height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 1rem;
    background: #101114;
  }

  .home-content {
    text-align: center;
  }

  .main-title {
    font-weight: 800;
    font-size: 3rem;
    letter-spacing: 0.2em;
    margin-bottom: 3rem;
    color: #ffffff;
  }

  .workout-buttons {
    display: flex;
    flex-direction: row;
    gap: 1.5rem;
    justify-content: center;
    align-items: center;
    margin-bottom: 2rem;
  }

  .workout-button {
    background-color: #21262d;
    border: 2px solid #30363d;
    color: #ffffff;
    font-weight: 700;
    font-size: 1.5rem;
    padding: 1.5rem 2rem;
    min-width: 200px;
    border-radius: 0.25rem;
    cursor: pointer;
    transition: all 0.2s ease;
    font-family: 'Montserrat', sans-serif;
  }

  .workout-button:hover {
    background-color: #ff0000;
    border-color: #ff0000;
    color: #ffffff;
    transform: scale(0.95);
  }

  .subtitle {
    color: #8b949e;
    font-size: 1.125rem;
    margin-top: 2rem;
  }

  /* Workout View Styles */
  .workout-view {
    min-height: 100vh;
    background-color: #0d1117;
    padding: 1rem;
    animation: fadeIn 0.3s ease-in-out;
  }

  .container {
    max-width: 64rem;
    margin: 0 auto;
  }

  .header {
    display: flex;
    align-items: center;
    margin-bottom: 2rem;
  }

  .back-button {
    background: none;
    border: none;
    padding: 0.5rem;
    margin-right: 1rem;
    cursor: pointer;
    border-radius: 0.25rem;
    transition: background-color 0.2s ease;
  }

  .back-button:hover {
    background-color: #21262d;
  }

  .back-icon {
    width: 1.5rem;
    height: 1.5rem;
    color: #ff0000;
  }

  .section-title {
    font-weight: 700;
    font-size: 2rem;
    color: #ffffff;
    margin: 0;
  }

  /* Warmup Section */
  .warmup-section {
    margin-bottom: 2rem;
  }

  .warmup-toggle {
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 1rem;
    background-color: #21262d;
    border: 1px solid #30363d;
    border-radius: 0.25rem;
    cursor: pointer;
    transition: background-color 0.2s ease;
    color: inherit;
    font-family: inherit;
  }

  .warmup-toggle:hover {
    background-color: #262c36;
  }

  .chevron {
    width: 1.5rem;
    height: 1.5rem;
    color: #ff0000;
    transition: transform 0.2s ease;
  }

  .chevron.expanded {
    transform: rotate(180deg);
  }

  .warmup-content {
    background-color: #161b22;
    border-left: 1px solid #30363d;
    border-right: 1px solid #30363d;
    border-bottom: 1px solid #30363d;
    border-bottom-left-radius: 0.25rem;
    border-bottom-right-radius: 0.25rem;
    padding: 1rem;
    animation: fadeIn 0.3s ease-in-out;
  }

  .warmup-list {
    list-style: none;
    margin: 0;
    padding: 0;
    display: flex;
    flex-direction: column;
    gap: 0.5rem;
  }

  .warmup-item {
    display: flex;
    align-items: flex-start;
    color: white;
  }

  .bullet {
    color: #ff0000;
    font-weight: 700;
    margin-right: 0.75rem;
  }

  /* Exercise Section */
  .workout-section {
    margin-bottom: 2rem;
  }

  .exercises {
    display: flex;
    flex-direction: column;
    gap: 1rem;
  }

  .exercise-card {
    background-color: #161b22;
    border: 1px solid #30363d;
    border-radius: 0.25rem;
    padding: 1.5rem;
    transition: border-color 0.2s ease;
  }

  .exercise-card:hover {
    border-color: rgba(255, 0, 0, 0.3);
  }

  .exercise-content {
    display: flex;
    flex-direction: column;
    gap: 1rem;
    align-items: center;
  }

  .illustration {
    width: 100%;
    max-width: 12rem;
    flex-shrink: 0;
  }

  :global(.exercise-svg) {
    width: 100%;
    height: 5rem;
    color: #ffffff;
  }

  :global(.exercise-label) {
    font-size: 8px;
    fill: currentColor;
    font-family: 'Montserrat', sans-serif;
    font-weight: 600;
  }

  .exercise-info {
    flex: 1;
  }

  .exercise-title {
    font-weight: 600;
    font-size: 1.25rem;
    color: #ffffff;
    margin-bottom: 0.5rem;
  }

  .exercise-sets {
    color: #8b949e;
  }

  .sets, .reps {
    font-weight: 600;
    color: #ff0000;
  }

  /* Responsive Design */
  @media (min-width: 768px) {
    .main-title {
      font-size: 4rem;
    }

    .workout-buttons {
      flex-direction: row;
    }

    .workout-button {
      font-size: 1.75rem;
    }

    .section-title {
      font-size: 2.5rem;
    }

    .exercise-content {
      flex-direction: row;
    }

    .illustration {
      width: 12rem;
    }

    :global(.exercise-svg) {
      height: 4rem;
    }

    .exercise-title {
      font-size: 1.5rem;
    }
  }

  /* Animations */
  @keyframes fadeIn {
    from {
      opacity: 0;
      transform: translateY(10px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
